"use strict";

document.addEventListener('DOMContentLoaded', function () {
  var checkBoxes = document.querySelectorAll('.check__input');
  checkBoxes.forEach(function (checkBox) {
    checkBox.addEventListener('focus', function (event) {
      event.currentTarget.classList.add('focus');
      document.querySelectorAll('.filters-check__text').forEach(function (text) {
        text.classList.remove('focus');
      });
      event.currentTarget.nextElementSibling.nextElementSibling.classList.add('focus');
      // console.log(event.currentTarget.nextElementSibling.nextElementSibling);
    });
  });

  /* -------------------------- */
  // TODO: реализовать эту функцию для всех кнопок КУПИТЬ
  var productPage = 'product-card.html';
  var btnsBuy = document.querySelectorAll('.catalog-card__btn');
  btnsBuy.forEach(function (btn) {
    btn.addEventListener('click', function (event) {
      var productType = event.target.previousSibling.previousElementSibling.previousElementSibling.textContent.trim().slice(1, length - 1);
      localStorage.setItem('breadСrumbs', JSON.stringify(productType));
    });
  });
  function productCheck() {
    var fileName = location.href.split("/").slice(-1);
    if (fileName[0] === productPage) {
      createBreadCrumbs();
    }
  }
  function createBreadCrumbs() {
    var productType = JSON.parse(localStorage.getItem('breadСrumbs'));
    var list = document.querySelector('.bread-crumbs__list');
    var listItem = document.createElement('li');
    var itemLink = document.createElement('a');
    var currentItemLink = document.querySelectorAll('.bread-crumbs__item-link');
    currentItemLink.forEach(function (link) {
      if (link.classList.contains('bread-crumbs__item-link_active')) {
        link.classList.remove('bread-crumbs__item-link_active');
      }
    });

    /* присваение классов */
    listItem.classList.add('bread-crumbs__list-item');
    itemLink.classList.add('bread-crumbs__item-link', 'another-links', 'bread-crumbs__item-link_active');

    /* отрисовка элементов */
    itemLink.textContent = productType;
    listItem.append(itemLink);
    list.append(listItem);
  }
  productCheck();
});
